# Static textual export: Graphviz DOT
def to_dot(edges_df, src="src", dst="dst"):
    lines = ["digraph G {"]
    for _, row in edges_df.iterrows():
        s = str(row[src]).replace('"', '\\"')
        d = str(row[dst]).replace('"', '\\"')
        lines.append(f'  "{s}" -> "{d}";')
    lines.append("}")
    return "\n".join(lines)

dot_text = to_dot(edges)
print(dot_text)
